export function updateInCollection(collection, index, updates) {
  if (index === undefined || index === null || index >= collection.length) {
    return [...collection, Object.assign({}, updates)];
  }
  return [
    ...collection.slice(0, index),
    Object.assign({}, collection[index], updates),
    ...collection.slice(index + 1),
  ];
}

export function updateCollection(collection, updates) {
  return collection.map((item) => Object.assign({}, item, updates));
}

export function popAtIndex(collection, index) {
  if (index < 0) {
    return collection.slice(0, index);
  }
  return [
    ...collection.slice(0, index),
    ...collection.slice(index + 1, collection.length),
  ];
}


export default {
  updateInCollection,
  updateCollection,
  popAtIndex,
};

import collections from './collections';
import reducers from './reducers';


/**
 * @namespace utils
 * @memberof Simpl
 */
export default {
  collections,
  reducers,
};

import { updateInCollection } from './collections';


export function updateObjectOrCollection(state, action, updates) {
  let collection;

  if (action.payload.key !== undefined) {
    collection = updateInCollection(state, action.payload.key, updates);
  } else {
    collection = Object.assign({}, state, updates);
  }
  return collection;
}

export default {
  updateObjectOrCollection,
};
